import React, {Component} from 'react';
import Pelicula from './Pelicula.js';
import Slider from './Slider.js';
import Sidebar from './Sidebar.js';

class Peliculas extends Component{

	state = {
		peliculas:[
			{titulo:'Batman V Superman', imagen:'data:image/jpeg;base64,'},
			{titulo:'Batman Begins',imagen:'data:image/jpeg;base64,'},
			{titulo:'Batman The Killing Joke',imagen:'https://target.scene7.com/is/image/Target/GUEST_4ca616c8-61df-4088-90e2-7b9ccb865e4e?wid=488&hei=488&fmt=pjpeg'}
		],
		nombre: "Rodo Lopez",
		favorita: {}
	}

	cambiarTitulo = () =>{
		var{peliculas} = this.state;
		peliculas[0].titulo = "Batman under the red hood";

		this.setState({
			peliculas: peliculas
		});
	}

	favorita = (pelicula,indice) =>{
		console.log("Favorita Marcada");		
		console.log(pelicula, indice);		
		this.setState({
			favorita:pelicula
		});
	}


	render(){
		var pStyle = {   			
			background:'green',
			color:'white',
			padding:'10px'
		};

		return(
			<React.Fragment>
			<Slider
			title="Peliculas"
			size="slider-small"
			/>
			<div className="center">
				<div id='content' className='peliculas'>
						<h2 className='subheader'>Listado de Peliculas</h2>
					<p>Seleccion de las Peliculas de {this.state.nombre}</p>
					<p>
						<button onClick={this.cambiarTitulo}>
						Cambiar de titulo de pelicula
						</button>
					</p>
					{
						this.state.favorita.titulo &&
							<p className="favorita" style={pStyle}>
								<strong>La pelicula favorita es: </strong>
								<span>{this.state.favorita.titulo}</span>
							</p>
					}

					{/*listado de peliculas*/}
					<div id = "articles" className="peliculas">
					{
						this.state.peliculas.map((pelicula,i) =>{
							return(
								<Pelicula 
									key={i} 
									pelicula= {pelicula}
									indice = {i}
									marcarFavorita={this.favorita}
								/>
							)
						})
					}
					</div>									
				</div>
				<Sidebar
				blog="false"
				/>
			</div>
			</React.Fragment>				
		);
	}
}
export default Peliculas;